<script>
  import { onMount } from "svelte";

  onMount(() => {
    const handleIntersection = (entries) => {
      entries.forEach((entry) => {
        // Zorg ervoor dat de SVG in de viewport is
        if (entry.isIntersecting) {
          entry.target.classList.add("golden");

          // Haal de id van het svg element op (of voeg een andere identificator toe)
          const svgId = entry.target.id;

          // Pas de tekst aan op basis van het id van de svg
          switch (svgId) {
            case "svg1":
              textEl.textContent =
                "The tool focuses on diplomatic engagement across the globe by analysing and comparing voting and speech behaviour on an international dyadic level.";
              break;
            case "svg2":
              textEl.textContent =
                "The data is analysed using AI techniques such as Named Entity Recognition and Sentiment Analysis.";
              break;
            case "svg3":
              textEl.textContent =
                "The tool provides an interactive dashboard powered by advanced algorithms, enabling users to explore and visualise global diplomatic patterns with precision and ease.";
              break;
            case "svg4":
              textEl.textContent =
                "Aims to transcend the limitations of traditional analyses, shedding light on the complex tapestry of relational dynamics that constitute our world.";
              break;
            // Add more cases here for other SVGs
            default:
              textEl.textContent =
                "The tool focuses on diplomatic engagement across the globe by analysing and comparing voting and speech behaviour on an international dyadic level.";
              break;
          }
        }
      });
    };

    const observer = new IntersectionObserver(handleIntersection, {
      threshold: 1,
      rootMargin: "0px 0px -120px 0px",
    });

    const svgElements = document.querySelectorAll(
      ".data-story:nth-of-type(4) svg"
    );
    const textEl = document.querySelector(
      ".data-story:nth-of-type(4) article p"
    );

    // Zorg ervoor dat textEl en svgElements bestaan
    if (!textEl || svgElements.length === 0) {
      console.error("Er zijn geen noodzakelijke elementen gevonden.");
      return;
    }

    svgElements.forEach((svg) => {
      observer.observe(svg);
    });

    return () => observer.disconnect();
  });
</script>

